import styled from 'styled-components';

const Container = styled.div`
  height:370px;
  width: 100vw;
  background: black;
  background-size: 100%;
  position: relative;
  overflow: hidden;
`;

const Logo = styled.div`
  height: 100vh;
  justify-content: center;
  display: flex;
`;

const BoxFooter = styled.div`
  width: ${props => props.width};
  display: flex;
  justify-content: ${props => props.center ? props.center: "block"}
  flex-direction: column;
  & > span: first-child {
    font-size: 16px;
    font-family: 'Work Sans', sans-serif;
    font-weight: bold;
    margin-bottom: 20px;
  }
  & > span {
    font-size: 16px;
    font-family: 'Hind', sans-serif;
    margin: 10px 0px;
  }
`

export {
  Container,
  Logo,
  BoxFooter
}
